css:
@import url('https://fonts.googleapis.com/css2?family=Almarai&display=swap');

  .info{
          border-collapse: collapse;
          border: 1;
          padding-bottom: -10px !important;
          margin-bottom: 10px !important;
          margin-top: -3px !important;
          border-radius: 5px 5px 0 0;
          overflow: hidden;
      }
 .info tbody tr{
          border-bottom: 1px solid #dddddd;
      }
.info tbody tr td{
        background-color: #f8f9fa !important;
      }
* {
    font-family: 'Almarai', sans-serif!important;
}

.head-table {
    font-size:16px;
    font-weight:bold;
    padding:0px 0px 0px 0px!important;
}

#footer-html {
    position:absolute!important;
    bottom:0!important;
    width:100%!important;


}

html:
{% set custom_letter_head = frappe.get_doc('Letter Head' , {'is_default': 1}) %}
{% if doc.mode_of_payment %}
{% set mode_of_payment = frappe.get_doc('Mode of Payment', doc.mode_of_payment)%}
{% else %}
{% set mode_of_payment = "" %}
{% endif %}
<div style="">
  {% if custom_letter_head.is_box == 0 %}
  {% if letter_head %}
  {{ letter_head }}
  {% endif %}
  <div style="width: 100%; padding-top: 20px;">
    <table style="width: 50%; text-align: right; font-size: 15px; border-collapse: collapse; float: right;">
      <tr>
        <td style="padding: 3px;">رقم المستند:</td>
        <td style="padding: 3px;">{{ doc.name }}</td>
      </tr>
      <tr>
        <td style="padding: 3px;">نوع المستند:</td>
        <td style="padding: 3px;">
          {% if mode_of_payment.type == "Cheque" %}
          {% if mode_of_payment.is_receivable_cheque == 1 %}
          اوراق قبض
          {% else %}
          اوراق دفع
          {% endif %}
          {% elif doc.payment_type == "Pay" %}
          سند صرف
          {% elif doc.payment_type == "Receive" %}
          سند قبض
          {% else %}
          سند تحويل
          {% endif %}
        </td>
      </tr>
    </table>

    <table style="width: 31%; text-align: right; font-size: 15px; border-collapse: collapse; float: left;">
      <tr>
        <td style="padding: 3px; font-weight : 900;">التاريخ:</td>
        <td style="padding: 3px; font-weight : 900;">{{ doc.posting_date }}</td>
      </tr>
      <tr>
        <td style="padding: 3px; font-weight : 900;">المبلغ:</td>
        <td style="padding: 3px; font-weight : 900; ">{{ doc.paid_amount }} {{ _(doc.paid_from_account_currency) }}</td>
      </tr>
    </table>
  </div>
  <div style="clear: both;"></div>
  {% else %}
  <div style="display: -webkit-box; justify-content: space-between; align-items: end;">
    <div style="display: flex; justify-content: space-between; align-items: end;">

      <div>
        <table class="info">
          <tr>
            <th style="padding: 3px; ">رقم المستند:</th>
            <td style="padding: 3px; ">{{ doc.name }}</td>
          </tr>
          <tr>
            <th style="padding: 3px; ">نوع المستند:</th>
            <td style="padding: 3px; ">
              {% if mode_of_payment.type == "Cheque" %}
              {% if mode_of_payment.is_receivable_cheque == 1 %}
              اوراق قبض
              {% else %}
              اوراق دفع
              {% endif %}
              {% elif doc.payment_type == "Pay" %}
              سند صرف
              {% elif doc.payment_type == "Receive" %}
              سند قبض
              {% else %}
              سند تحويل
              {% endif %}
            </td>
          </tr>
          <tr>
            <th style="padding: 3px; ">التاريخ:</th>
            <td style="padding: 3px; ">{{ doc.posting_date }}</td>
          </tr>
          <tr>
            <th style="padding: 3px; ">المبلغ:</th>
            {% if doc.payment_type == "Receive" %}
            <td style="padding: 3px; ">{{ doc.received_amount }} {{ _(doc.paid_to_account_currency) }}</td>
            {% else %}
            <td style="padding: 3px; ">{{ doc.paid_amount }} {{ _(doc.paid_from_account_currency) }}</td>
            {% endif %}
          </tr>
        </table>
      </div>
    </div>
    <div style="text-align: left; width: 50%;">
      {% if letter_head %}
      {{ letter_head }}
      {% endif %}
    </div>
  </div>
  {% endif %}
  <div style="display: flex; gap: 5px; flex-direction: column; font-size: 15px; font-weight: bold;">

    {% if doc.payment_type in ["Pay", "Receive"] %}
    <div style="display: flex;">
      <div style="width: 18%;">{% if doc.payment_type == "Pay" and doc.multi_expense == 0 %}يصــــرف إلى :{% elif
        doc.payment_type == "Receive" %}استلمنا مــــن :{% endif %}</div>
      <div style="flex: 1; background-color: #f8f9fa; font-size: 14px;">{{ doc.party_name or "" }}</div>
    </div>
    {% else %}
    <div style="display: flex;">
      <div style="width: 18%;">مــــــــــــن :</div>
      <div style="flex: 1; background-color: #f8f9fa; font-size: 14px;">{{ doc.paid_from }}</div>
    </div>
    <div style="display: flex;">
      <div style="width: 18%;"> إلــــــــــــى :</div>
      <div style="flex: 1; background-color: #f8f9fa; font-size: 14px;">{{ doc.paid_to }}</div>
    </div>
    {% endif %}

    {% if doc.payment_type == "Pay" %}
    <div style="display: flex;">
      <div style="width: 18%;">من حساب :</div>
      <div style="flex: 1; background-color: #f8f9fa; font-size: 14px;">{{ doc.paid_from }}</div>
    </div>
    {% elif doc.payment_type == "Receive" %}
    <div style="display: flex;">
      <div style="width: 18%;">إلى حساب :</div>
      <div style="flex: 1; background-color: #f8f9fa; font-size: 14px;">{{ doc.paid_to }}</div>
    </div>
    {% endif %}
    {% if doc.custom_remarks %}
    <div style="display: flex;">
      <div style="width: 18%;">وذلــــك مقابـــــل :</div>
      <div style="flex: 1; background-color: #f8f9fa; font-size: 14px;">{{ doc.remarks }}</div>
    </div>
    {% endif %}


    <div style="display: flex;">
      <div style="width: 18%;">تفقيط المبلغ :</div>
      <div style="flex: 1; background-color: #f8f9fa; font-size: 14px;">{{ money_to_words(doc.paid_amount) }}</div>
    </div>

    {% if mode_of_payment.type == "Cheque" %}
    {% if doc.bank_name or doc.payee_name %}
    <div style="display: flex;">
      {% if doc.bank_name %}
      <div style="width: 18%;">البنك :</div>
      <div style="width: 32%; background-color: #f8f9fa;">{{ doc.bank_name }}</div>
      {% endif %}

      {% if doc.payee_name %}
      <div style="width: 18%;">اسم المستفيد :</div>
      <div style="width: 32%; background-color: #f8f9fa;">{{ doc.payee_name }}</div>
      {% endif %}
    </div>
    {% endif %}
    {% endif %}
    {% if doc.payment_type in ["Pay", "Receive", "Internal Transfer"] %}
    {% if doc.reference_no or doc.reference_date %}
    <div style="display: flex;">
      {% if doc.reference_no %}
      <div style="width: 18%;">الرقم المرجعي :</div>
      <div style="width: 32%; background-color: #f8f9fa;">{{ doc.reference_no }}</div>
      {% endif %}

      {% if doc.reference_date %}
      <div style="width: 18%;">تاريخ الإستحقاق :</div>
      <div style="width: 32%; background-color: #f8f9fa;">{{ doc.reference_date }}</div>
      {% endif %}
    </div>
    {% endif %}
    {% endif %}


  </div>

  {% set multi_expense = doc.multi_expense %}
  {% if multi_expense == 1 %}
  <div style="margin-top: 20px;margin-bottom: 20px;">
    <table class="info" width="100%">
      <thead>
        <tr style="background-color: #f8f9fa;">
          <th style="padding: 8px; text-align: center; border: 1px solid #dee2e6; ">الحساب</th>
          <th style="padding: 8px; text-align: center; border: 1px solid #dee2e6; ">{{_("Party Type")}} / {{_("Party")}}</th>
          <th style="padding: 8px; text-align: center; border: 1px solid #dee2e6; ">مركز التكلفة</th>
          <th style="padding: 8px; text-align: center; border: 1px solid #dee2e6;width: 10%; ">ملاحظات</th>
          <th style="padding: 8px; text-align: center; border: 1px solid #dee2e6; ">المبلغ</th>
        </tr>
      </thead>
      <tbody>
        {% for expense in doc.company_expense %}
        <tr>
          <td style="padding: 5px; text-align: right; border: 1px solid #dee2e6; ">{{ expense.default_account or "" }}
          </td>
          <td style="padding: 5px; text-align: right; border: 1px solid #dee2e6;">
            {% if expense.party_type %}{{ expense.party_type }} :{% endif %}
            <br>
            {{ expense.party or "" }}
        </td>        
          <td style="padding: 5px; text-align: right; border: 1px solid #dee2e6; ">{{ expense.cost_center or "" }}</td>
          <td style="padding: 5px; text-align: right; border: 1px solid #dee2e6; word-wrap: break-word; max-width: 300px;">{{ expense.remarks or "" }}</td>
          <td style="padding: 5px; text-align: right; border: 1px solid #dee2e6; ">{{
            frappe.format_value(expense.amount, {'fieldtype': 'Currency'}) }}</td>
        </tr>
        {% endfor %}
      </tbody>
    </table>
  </div>
  {% endif %}
  <table width="100%" style="text-align:center;margin-top:5px;font-size:14px;font-weight:bold;">
    <tbody>
      <tr>
        <td width="33%">اسم المستلم</td>
        <td width="33%">التوقيـــــــــــــــــــــــع</td>
        <td width="33%">الأدارة المالية</td>
      </tr>

    </tbody>
  </table>
</div>
